$(document).ready(function () {
    $("img, a").on("dragstart", function (b) {
        b.preventDefault()
    });


    $('.b1').click(function () {
        $("html, body").animate({scrollTop: $("#box1").offset().top - 300}, 1000);
        return false;
    });
    $('.b2').click(function () {
        $("html, body").animate({scrollTop: $("#about").offset().top - 300}, 1000);
        return false;
    });
    $('.b3').click(function () {
        $("html, body").animate({scrollTop: $("#otziv").offset().top - 400}, 1000);
        return false;
    });
    $('.b4').click(function () {
        $("html, body").animate({scrollTop: $("#wrap-order-form").offset().top - 300}, 1000);
        return false;
    });


});